import InputField from "../input-field/InputField";
import Icon from "../icon/Icon";
import Button from "../button/Button";
import React, {Component} from "react";

interface StateInterface {
    isLoading: boolean
}

class LoginForm extends Component<{}, StateInterface> {
    state: StateInterface = {
        isLoading: false
    };

    submitFormHandler = (event: any) => {
        event.preventDefault();
        const {target} = event;
        const username = target.username.value;
        const password = target.password.value;

        if (username && password) {
            this.setState({
                isLoading: true
            });
        }

        console.log(username, password);
    };

    render() {
        return (
            <form className="form" onSubmit={this.submitFormHandler}>
                <InputField type={'text'} name={'username'} placeholder={'Username'}>
                    <Icon name={'username'} color={'#CCC'} width={14} height={16}/>
                </InputField>

                <InputField type={'password'} name={'password'} placeholder={'Password'}>
                    <Icon name={'password'} color={'#CCC'} width={14} height={16}/>
                </InputField>

                <Button title={'Log in'}
                        extraClasses="w-100"
                        buttonStyle="success"
                        type="submit"
                        isLoading={this.state.isLoading}
                />
            </form>
        )
    }
}

export default LoginForm;
