import {FETCH_SERVERS} from "../actions";

const initState = {
    items: []
};

export default function(state = 0, action: any) {
    switch (action.type) {
        case FETCH_SERVERS:
            return {
                state,
                items: action.payload
            };
        default:
            return state;
    }
}
