import React from "react";
import './Button.scss';

const Button: React.FC<{
    title: string,
    buttonStyle?: 'default' | 'success' | 'danger',
    extraClasses?: string,
    type?: 'button' | 'submit',
    isLoading?: boolean
}> = (props) => {
    return (
        <button className={`button button--${props.buttonStyle} ${props.extraClasses}`}
                type={props.type}
                disabled={props.isLoading}
        >
            {props.title}
        </button>
    );
};

Button.defaultProps = {
    type: 'button',
    buttonStyle: 'default'
};

export default Button;
