import React from "react";
import './InputField.scss';

const InputField: React.FC<{
    type: string,
    name: string,
    placeholder?: string,
    extraClasses?: string,
    error?: string | null
}> = (props) => {
    return (
        <div className="form-group">
            <input type={props.type}
                   name={props.name}
                   className={`form-control ${props.extraClasses} ${props.error ? 'form-control--error' : ''}`}
                   placeholder={props.placeholder}
            />
            { !!props.children ?
                <span className="form-group__icon">
                    {props.children}
                </span>
                : null
            }
            { props.error ?
                <label className="form-control__label form-control__label--error">Required</label>
                : null
            }
        </div>
    );
};

export default InputField;
