import React, {Component} from "react";
import "./ServerList.scss";

interface StateInterface {
    data: ServerInterface[];
}

export interface ServerInterface {
    name: string;
    distance: number;
}

class ServerList extends Component<{}, StateInterface> {
    state: StateInterface = {
        data: []
    };

    async componentDidMount() {
        const token = await localStorage.getItem('jwtToken');
        const response = await fetch('http://playground.tesonet.lt/v1/servers', {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + token,
            }
        });

        const data = await response.json();

        this.setState({
            data
        });
    }

    createList() {
        const servers = this.state.data;

        const serversList = servers.map((server, i) => {
            return (
                <tr key={i}>
                    <th scope="row">{server.name}</th>
                    <td className="text-right">{server.distance}</td>
                </tr>
            )
        });

        console.log(serversList);

        return serversList;
    }

    render() {
        return (
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Server</th>
                            <th scope="col" className="text-right">Distance</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.createList()}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default ServerList;
