export interface AuthStateInterface {
    isAuth: boolean;
    token?: string;
}

const authReducer = (state: AuthStateInterface = {isAuth: false}, action: any) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                isAuth: true,
                token: action.user
            };
        case 'LOGOUT':
            return {
                isAuth: true,
                token: action.user
            };
        default:
            return state;
    }
};

export default authReducer;
