import InputField from "../InputField/InputField";
import Icon from "../Icon/Icon";
import Button from "../Button/Button";
import React, {Component} from "react";
import {withRouter} from "react-router-dom";
import {localStorageKey} from "../../constants/auth.constants";

interface ErrorInterface {
    username?: string;
    password?: string;
}

interface StateInterface {
    isLoading: boolean;
    formErrors?: ErrorInterface;
}

class LoginForm extends Component<any, StateInterface> {
    state: StateInterface = {
        formErrors: {},
        isLoading: false
    };

    login = async (body: { username: string, password: string }) => {
        this.setState({
            isLoading: true
        });

        const response = await fetch('http://playground.tesonet.lt/v1/tokens', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(body)
        });

        const data = await response.json();

        if (data) {
            // Successful login
            if (data.token) {
                console.log(data.token);
                localStorage.setItem(localStorageKey, data.token);
                // this.props.history.push('/');
            } else if (data.message) {
                console.log(data.message);
            }
        }
    };

    submitFormHandler = async (event: any) => {
        event.preventDefault();
        const {target} = event;
        const username = target.username.value;
        const password = target.password.value;

        let errors: ErrorInterface = {};

        if (!username) {
            errors = {...errors, username: 'Username is required'};
        }

        if (!password) {
            errors = {...errors, password: 'Password is required'};
        }

        if (errors) {
            this.setState({
                formErrors: errors
            });
            return;
        }

        if (username && password) {
            this.login({username, password});
        }
    };

    render() {
        return (
            <form className="form" onSubmit={this.submitFormHandler}>
                <InputField type={'text'}
                            name={'username'}
                            placeholder={'Username'}
                            error={() => this.state.formErrors.find(r => r.field === 'username')}
                            onChange={() => this.setState({})}
                >
                    <Icon name={'username'} color={'#CCC'} width={14} height={16}/>
                </InputField>

                <InputField type={'password'}
                            name={'password'}
                            placeholder={'Password'}
                            extraClasses={'danger'}
                            error={this.state.form.password.error}
                >
                    <Icon name={'password'} color={'#CCC'} width={14} height={16}/>
                </InputField>

                <Button title={'Log in'}
                        extraClasses="w-100"
                        buttonStyle="success"
                        type="submit"
                        isLoading={this.state.isLoading}
                />
            </form>
        )
    }
}

export default withRouter(LoginForm);
